import { useState } from "react";
import "./App.css";
import { MovieForm } from "./components/add-movie-form/movieform";
import { MovieCard } from "./components/card";

function App() {
  let data = [
    {
      title: "spiderman",
      discription:
        "Jammu and Kashmir was a state of India from 1954 to 2019, constituting the southern and southeastern portion of the larger Kashmir region, which has been the subject of a dispute between India, Pakistan and China since the mid-20th century. ",
      URL: "https://www.worldatlas.com/r/w768-q80/upload/70/33/ab/shutterstock-115227475.jpg",
    },
    {
      title: "Batman",
      discription:
        "Islamabad is the capital city of Pakistan, and is administered by the Pakistani federal government as part of the Islamabad Capital Territory. It is the ninth-largest city in Pakistan with population of about 4.1 million people.",
      URL: "https://www.thenews.com.pk/assets/uploads/updates/2021-03-17/805662_2993568_339964_794478_updates_updates.jpg",
    },
    {
      title: "Superman",
      discription:
        "Lahore is the capital of the Pakistani province of Punjab and is the country's 2nd largest city after Karachi, as well as the 26th largest city in the world. Lahore is one of Pakistan's wealthiest cities with an estimated GDP of $84 billion as of 2019.",
      URL: "https://www.thediaryofanomad.com/wp-content/w3-webp/uploads/2020/04/best-places-to-visit-in-lahore-badshahi-mosque.jpgw3.webp",
    },
  ];

  const [movieData, setMovieData] = useState(data);
  const [renderer, setRenderer] = useState("");

  const newMovie = (Movie) => {
    data.push(Movie);
    setMovieData(data);
    //console.log(data);
    renderMovie();
  };

  const renderMovie = () => {
    setRenderer(
      movieData.map((movie) => (
        <MovieCard
          name={movie.title}
          discription={movie.discription}
          url={movie.URL}
        />
      ))
    );
  };

  //renderMovie();

  return (
    <div className="App">
      <div className="movie-form">
        <MovieForm newMovieInfo={newMovie} />
      </div>

      <div className="moviecard-display">
        {
          renderer
          /* {data.map((movie) => (
          <MovieCard
            name={movie.title}
            discription={movie.discription}
            url={movie.URL}
          />
        ))} */
        }
      </div>
    </div>
  );
}

export default App;
