import React, { useState } from "react";
import { AiOutlineClose } from "react-icons/ai";
import "./movieform.css";

export const MovieForm = (props) => {
  const [title, setTitle] = useState("zain");
  const [releaseDate, setReleaseDate] = useState("");
  const [timeDuration, setTimeDuration] = useState("");
  const [gener, setGener] = useState("");
  const [age, setAge] = useState("");
  const [imageURL, setImageURL] = useState("");

  const movieInfo = {
    id: Date.now(),
    title: title,
    releaseDate: releaseDate,
    timeDuration: timeDuration,
    gener: gener,
    age: age,
    URL: imageURL,
  };

  const isFieldsEmpty = () => {
    if (
      title === "" ||
      releaseDate === "" ||
      timeDuration === "" ||
      gener === "" ||
      age === "" ||
      imageURL === ""
    ) {
    } else {
      props.show(false);
    }
  };

  return (
    <div className="Movie-Form">
      <AiOutlineClose
        className="form-close"
        onClick={() => props.show(false)}
      />
      <h1 className="form-name">Add Movie</h1>
      <form action="" className="movie-info">
        <div className="movie-title info">
          <span>Title</span>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter movie name..."
          />
        </div>

        <div className="movie-release-date info">
          <span>Release Date</span>
          <input
            type="text"
            onChange={(e) => setReleaseDate(e.target.value)}
            placeholder="dd-mmm-yyyy"
          />
        </div>
        <div className="movie-time info">
          <span>Time Duration</span>
          <input
            type="text"
            onChange={(e) => setTimeDuration(e.target.value)}
            placeholder="00h 00m"
          />
        </div>
        <div className="movie-gener info">
          <span>Gener</span>
          <input
            type="text"
            onChange={(e) => setGener(e.target.value)}
            placeholder="Movie gener..."
          />
        </div>

        <div className="movie-age info">
          <span>Age Limit</span>
          <input
            type="text"
            onChange={(e) => setAge(e.target.value)}
            placeholder="Age+"
          />
        </div>

        <div className="movie-image-url info">
          <span>Image URL</span>
          <input
            type="text"
            onChange={(e) => setImageURL(e.target.value)}
            placeholder="Enter movie image url..."
          />
        </div>
      </form>
      <div className="add-movie-btn">
        <button
          //onClick={isFieldsEmpty()}
          onClick={() => props.newMovieInfo(movieInfo)}
        >
          <span>ADD MOVIE</span>
        </button>
      </div>
    </div>
  );
};
