import React from "react";
import "./App.css";
import { MovieForm } from "./components/add-movie-form/movieform";
import { MovieCard } from "./components/card";
import { useState } from "react";

function App() {
  let data = [
    {
      id: "10",
      title: "Rustom",
      URL: "https://static-koimoi.akamaized.net/wp-content/new-galleries/2016/03/rustom-movie-posters.jpg",
      releaseDate: "12-Aug-2016",
      timeDuration: "02h 28m",
      gener: "Thriller Crime",
      age: "16+",
    },
    {
      id: "11",
      title: "Money Heist",
      URL: "https://images.thequint.com/thequint%2F2021-09%2Fb9aab177-f368-4185-91b4-b37f15c2b7ee%2Fmoneyheist.png?auto=format%2Ccompress&fmt=webp",
      releaseDate: "02-May-2017",
      timeDuration: "5 Seasons",
      gener: "Crime Suspenseful Exciting",
      age: "16+",
    },
    {
      id: "12",
      title: "Lucifer",
      URL: "https://releasedate.news/wp-content/uploads/2021/05/Lucifer-Season-6-What-Netflix-Release-Date-A-planned-sequel.jpg",
      releaseDate: "25-jan-2016",
      timeDuration: "6 Seasons",
      gener: "Detective fantasy Comedy-drama",
      age: "18+",
    },
  ];

  let dummy = [
    {
      id: "",
      title: "",
      URL: "",
      releaseDate: "",
      timeDuration: "",
      gener: "",
      age: "",
    },
  ];

  const [movieData, setMovieData] = useState(data);
  const [editMovie, setEditMovie] = useState();
  const [show, setShow] = useState(false);

  const addMovie = () => {
    setEditMovie(dummy);
    setShow(true);
  };

  const eidtMovie = (movieId) => {
    setEditMovie(
      movieData.filter((movie) => {
        return movie.id === movieId;
      })
    );
    setShow(true);
  };

  const deleteMovie = (movieId) => {
    setMovieData(
      movieData.filter((movie) => {
        return movie.id !== movieId;
      })
    );
  };

  const newMovie = (Movie, addMovie) => {
    //console.log(addMovie);
    if (addMovie) {
      let notReplace = true;
      let temp = [];
      movieData.map((movie) => {
        if (movie.id === Movie.id) {
          notReplace = false;
          return temp.push(Movie);
        } else {
          return temp.push(movie);
        }
      });
      if (notReplace) {
        temp.push(Movie);
      }
      setMovieData(temp);
    }
  };

  const closeForm = (show) => {
    console.log(show);
    setShow(show);
  };

  return (
    <div className="App">
      {show ? (
        <div className="movie-form-area">
          <div className="movie-form">
            {editMovie.map((movie) => (
              <MovieForm
                id={movie.id}
                title={movie.title}
                URL={movie.URL}
                releaseDate={movie.releaseDate}
                timeDuration={movie.timeDuration}
                gener={movie.gener}
                age={movie.age}
                newMovieInfo={newMovie}
                show={closeForm}
              />
            ))}
          </div>
        </div>
      ) : null}

      <div className="header">
        <button className="header-add-movie-btn" onClick={addMovie}>
          Add Movie
        </button>
      </div>

      <div className="moviecard-display">
        {movieData.map((movie) => (
          <MovieCard
            id={movie.id}
            title={movie.title}
            URL={movie.URL}
            releaseDate={movie.releaseDate}
            timeDuration={movie.timeDuration}
            gener={movie.gener}
            age={movie.age}
            delete={deleteMovie}
            edit={eidtMovie}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
